mapper.select:
SELECT 
    u.id, 
    u.avatar, 
    u.username, 
    u.real_name, 
    u.status, 
    u.create_time, 
    u.description
FROM sys_user u
WHERE 1=1
AND u.username LIKE #{request.username}
AND u.id = #{request.id}
AND u.real_name LIKE #{request.realName}
AND u.status = #{request.status}
AND u.description LIKE #{request.remark}
AND u.create_time >= #{request.startTime}
AND u.create_time <= #{request.endTime}

        SELECT COUNT(*)
        FROM sys_user u
        WHERE 1 = 1

        
            AND u.username LIKE CONCAT('%', #{request.username}, '%')
        

        
            AND u.id = #{request.id}
        

        
            AND u.real_name LIKE CONCAT('%', #{request.realName}, '%')
        

        
            AND u.status = #{request.status}
        

        
            AND u.description LIKE CONCAT('%', #{request.remark}, '%')
        

        
            AND u.create_time  >=  #{request.startTime}
        

        
            AND u.create_time  <=  #{request.endTime}
